package listeners;

import com.aventstack.extentreports.ExtentReports;
import com.aventstack.extentreports.reporter.ExtentHtmlReporter;
import com.aventstack.extentreports.reporter.configuration.ChartLocation;
import com.aventstack.extentreports.reporter.configuration.Protocol;
import com.aventstack.extentreports.reporter.configuration.Theme;

import java.io.File;
import java.text.SimpleDateFormat;
import java.util.Date;

public class ExtentManager {
    private static final String OUTPUT_FOLDER = "test-output/";
    private static final String timeStamp = new SimpleDateFormat("dd.MM.yyyy_HH.mm.ss").format(new Date());
    private static final String FILE_NAME = "MFS Automation Report_" + timeStamp + ".html";


    static ExtentReports reportSetup() {
        ExtentHtmlReporter htmlReporter = new ExtentHtmlReporter(OUTPUT_FOLDER + FILE_NAME);
        htmlReporter.config().setDocumentTitle("MFS Africa - QA Report");
        String image = "data:image/png;base64,";
        htmlReporter.config().setReportName("<a target='_blank' href='https://mfsafrica.com/'><div style='border-radius: 9px; border:3px solid #15af9f; float:right; background:url("+ image +") #fff; background-repeat:no-repeat; background-size:contain; height:50px;width:146px; display:inline-block;'></div></a> <b class='label suite-start-time blue darken-3'>MFS Africa - Automated Tests Report</b> ");
        htmlReporter.config().setTimeStampFormat("dd MMM yyyy hh:mm:ss a");
        htmlReporter.config().setTestViewChartLocation(ChartLocation.BOTTOM);
        htmlReporter.config().setChartVisibilityOnOpen(true);
        htmlReporter.config().setProtocol(Protocol.HTTPS);
        htmlReporter.config().setTheme(Theme.DARK);

        File directory = new File(OUTPUT_FOLDER);
        if (! directory.exists()){
            directory.mkdir();
            // If you require it to make the entire directory path including parents,
            // use directory.mkdirs(); here instead.
        }

        ExtentReports extent = new ExtentReports();
        extent.attachReporter(htmlReporter);
        extent.setReportUsesManualConfiguration(true);

        extent.setSystemInfo("OS",System.getProperty("os.name"));
        extent.setSystemInfo("Host Name", "tmagwenya-cas-lap" );
        extent.setSystemInfo("Environment","MFS Africa Automated Test Environment");
        extent.setSystemInfo("Tester Name",System.getProperty("user.name"));
        extent.setSystemInfo("Report Name","MFS Africa Automated Tests Report");

        return extent;
    }
}